mapper.select:
select count(*) from contri_info_tb t1 left join user_tb t2 on t1.user_id = t2.id where 1=1
and user_id = #{paramsDto.userId,jdbcType=BIGINT}
and UPPER(t2.name) like CONCAT("%",#{paramsDto.userName},"%")

select t1.id, t2.name AS userName, t1.contri_amount AS contriAmount, t1.contri_things AS contriThings, t1.contri_time AS contriTime, t1.mobile AS userMobile, t1.org_name AS orgName, t1.org_r_name AS orgRName, t1.contri_proof AS contriProof, t1.contri_type AS contriType, t1.update_time AS updateTime, t1.is_delete AS isDelete, t1.contri_state AS contriState, t1.if_stamp AS ifStamp, t1.create_time AS createTime, t1.contri_value AS contriValue from contri_info_tb t1 left join user_tb t2 on t1.user_id = t2.id where 1=1
and t1.id = #{paramsDto.contriInfoId,jdbcType=BIGINT}
and t1.user_id = #{paramsDto.userId,jdbcType=BIGINT}
and t1.contri_state = #{paramsDto.contriState,jdbcType=BIGINT}
and UPPER(t2.name) like CONCAT("%",#{paramsDto.userName},"%")
and t1.contri_time >= #{paramsDto.startTime}
and t1.contri_time <= #{paramsDto.endTime}
order by t1.update_time desc
limit #{paramsDto.offSet,jdbcType=INTEGER},#{paramsDto.limit,jdbcType=INTEGER}
