mapper.select:
select

from user_tb


    name like "%"#{user.name,jdbcType=VARCHAR}"%"


    and id_card_number like "%"#{user.idCardNumber,jdbcType=VARCHAR}"%"


    and mobile like "%"#{user.mobile,jdbcType=VARCHAR}"%"


    and state = #{user.state,jdbcType=INTEGER}


    and type = #{user.type,jdbcType=INTEGER}


    and role_id = #{user.roleId,jdbcType=BIGINT}


    and sex = #{user.sex,jdbcType=INTEGER}


    and address like "%"#{user.address,jdbcType=VARCHAR}"%"


    and org_name like "%"#{user.orgName,jdbcType=VARCHAR}"%"


    and old_name like  "%"#{user.oldName,jdbcType=VARCHAR}"%"


    and old_mobile like "%"#{user.oldMobile,jdbcType=VARCHAR}"%"


    and school like "%"#{user.school,jdbcType=VARCHAR}"%"


    and is_delete = #{user.isDelete,jdbcType=INTEGER}


order by ${field} ${sort}
limit #{start} , #{pageSize}

mapper.update:
update user_tb set love_point=
(select sum(love_points) from love_points_tb where user_id=#{userId,jdbcType=BIGINT} and is_delete=0)
where id= #{userId,jdbcType=BIGINT}
